DROP TABLE IF EXISTS email_manager;
CREATE TABLE email_manager (
  em_id int(11) NOT NULL AUTO_INCREMENT,
  em_name varchar(255) DEFAULT NULL,
  em_language int(11) DEFAULT NULL,
  em_body text,
  em_delete tinyint(1) NOT NULL DEFAULT 0,
  em_type varchar(255) DEFAULT NULL,
  em_body_txt text,
  PRIMARY KEY (em_id)
) ENGINE=MyISAM;

INSERT INTO email_manager (em_id, em_name, em_language, em_body, em_delete, em_type, em_body_txt) VALUES
(64, 'change_password_mail', 1, '<table  width="100%" border="0" align="center" cellpadding="4" cellspacing="0">\n  <tr> \n    <td style="border-bottom: 1px solid; border-color: #cccccc;"><div align="right"><img src="{$logo_path}logo.gif"></div></td>\n  </tr>\n  <tr> \n    <td><p><font size="2" face="Verdana, Arial, Helvetica, sans-serif"><strong>Dear Customer,</strong></font> </p>\n      <p><font size="2" face="Verdana, Arial, Helvetica, sans-serif">Your password was successfully changed.<br />\n        <br />\n  Your new login data:</font>\n      </p>      \n      <table width="100%"  border="0" bgcolor="f1f1f1">\n    <tr>\n      <td> <font size="1" face="Verdana, Arial, Helvetica, sans-serif"><strong>Your e-mail-address:</strong> {$EMAIL}\n            <br />\n            <strong>Your new password:</strong> {$PASSWORD}\n        </font> </td>\n    </tr>\n  </table></td>\n  </tr>\n</table>\n\n 	  	 \n', 0, 'mail', '\nDear Customer,\n\nYour password was successfully changed.\n\nYour new login data:\n      \nYour e-mail-address: {$EMAIL}\nYour new password: {$PASSWORD}\n'),
(65, 'create_account_mail', 1, '<table  width="100%" border="0" align="center" cellpadding="4" cellspacing="0">\n  <tr> \n    <td style="border-bottom: 1px solid; border-color: #cccccc;"><div align="right"><img src="{$logo_path}logo.gif"></div></td>\n  </tr>\n  <tr> \n    <td><p><font size="2" face="Verdana, Arial, Helvetica, sans-serif"><strong>Your account has been successfully created!</strong></font> <br />\n        <br />\n        <font size="2" face="Verdana, Arial, Helvetica, sans-serif">You now have access to the following features:</font><br /> \n        <br />\n        <font size="2"><b><font face="Verdana, Arial, Helvetica, sans-serif">-Shopping cart</font></b><font face="Verdana, Arial, Helvetica, sans-serif"> - Products placed in the shopping cart will remain there until they''ve been deleted or purchased.<br />\n        <b>-Address book</b> - The address book allows you to save several different shipping destinations.<br />\n        <b>-Order history</b> - Your order history is always available for you.<br />\n        <b>-Product evaluation</b> - Rate and comment our products!</font></font></p>\n      <p>\n        <font size="2" face="Verdana, Arial, Helvetica, sans-serif">If this account wasn''t created by you, please contact us at \n        <A HREF="mailto:{$content.MAIL_REPLY_ADDRESS}">{$content.MAIL_REPLY_ADDRESS}</A>\n        . <br />\n    <br />\n    {if $SEND_GIFT==true}\n      <br />\nAs a thank you for creating your account, you''ve received a <b>{$GIFT_AMMOUNT}</b> gift voucher!<br />\n<br />\nRedeem your voucher with the following code <b>{$GIFT_CODE}</b> when placing an order or simply by clicking the following link <a href="{$GIFT_LINK}">[redeem Voucher]</a>.{/if} {if $SEND_COUPON==true} As a thank you for creating your account, you''ve recieved a discount voucher!<br />\nThe voucher details are:<br />\n<b>{$COUPON_DESC}</b> <br />\nRedeem your voucher by entering the code <b>{$COUPON_CODE}</b> during checkout process, when asked for it.{/if} \n<br />If you have any questions, please contact us at <A HREF="mailto:{$content.MAIL_REPLY_ADDRESS}">{$content.MAIL_REPLY_ADDRESS}</A> !\n</font></p></td>\n  </tr>\n</table>\n\n 	  	 \n', 0, 'mail', 'Your account has been successfully created!\n\nYou now have access to the following features:\n\n  SHOPPING CART - Products placed in the shopping cart will remain there until they''ve been deleted or purchased.\n  ADDRESS BOOK - The address book allows you to save several different shipping destinations.\n  ORDER HISTORY - Your order history is always available for you.\n  PRODUCT EVALUATION - Rate and comment our products!\n\nIf this account wasn''t created by you, please contact us at {$content.MAIL_REPLY_ADDRESS}.\n\n{if $SEND_GIFT==true}\nAs a thank you for creating your account, you''ve received a {$GIFT_AMMOUNT} gift voucher! \nRedeem your voucher with the following code - {$GIFT_CODE} - when placing an order or simply by clicking the following link [redeem Voucher].{/if}{if $SEND_COUPON==true}\nAs a thank you for creating your account, you''ve recieved a discount voucher!\nThe voucher details are:\n{$COUPON_DESC}\nRedeem your voucher by entering the code {$COUPON_CODE} during checkout process, when asked for it.{/if} \n\nIf you have any questions, please contact us at {$content.MAIL_REPLY_ADDRESS}!'),
(66, 'new_password_mail', 1, '<table  width="100%" border="0" align="center" cellpadding="4" cellspacing="0">\n  <tr> \n    <td><p><font size="2" face="Verdana, Arial, Helvetica, sans-serif"><b>You''ve received a new password!</b></p> \n      <p>Log in with your new password <b>{$NEW_PASSWORD}</b> in order to change it. Should you have any difficulties, please contact us!\n  </tr>\n</table>\n\n 	  	 \n', 0, 'mail', '\nYou''ve received a new password!\n\nLog in with your new password - {$NEW_PASSWORD} - in order to change it. Should you have any difficulties, please contact us!'),
(67, 'newsletter_mail', 1, '<p><font size="2" face="Verdana, Arial, Helvetica, sans-serif"><b>Thank you for subscribing!</b></p>\n<p>Please click the following activation link to receive newsletters. If you haven''t subscribed to this service, please ignore this e-mail!</p>\n<dl><dt><b>Your activation link:</b></dt>\n<dd><a href="{$LINK}">{$LINK}</a></dd>\n</dl>', 0, 'mail', '\nThank you for subscribing!\n\nPlease click the following activation link to receive newsletters. If you haven''t subscribed to this service, please ignore this e-mail!\n\nYour activation link:\n{$LINK}'),
(68, 'order_mail', 1, '{config_load file="$language/lang_$language.conf" section="duty_info"} \n<table width="100%" border="0">\n  <tr> \n    <td>\n      <table width="100%" border="0" cellpadding="0" cellspacing="0">\n        <tr> \n          <td><font size="1" face="Verdana, Arial, Helvetica, sans-serif">{$address_label_customer}<br />\n            <br />\n            {if $PAYMENT_METHOD}<strong>Paymentmethod:</strong> {$PAYMENT_METHOD}<br />{/if}\n            <strong>Order No.:</strong> {$oID}<br />\n            <strong>Orderdate:</strong> {$DATE}<br />\n            {if $csID}<strong>Customer ID:</strong> {$csID}<br />{/if}\n            <strong>Your e-mail-address:</strong> {$EMAIL}<br />\n            </font>\n          </td>\n          <td width="1"><img src="{$logo_path}logo.gif"></td>\n        </tr>\n      </table>\n      <br /> \n      <table style="border-top:1px solid; border-bottom:1px solid;" width="100%" border="0">\n        <tr bgcolor="#f1f1f1"> \n          <td width="50%"> <p><font size="1"><strong><font face="Verdana, Arial, Helvetica, sans-serif">Shippingaddress<br />\n          </font></strong></font></p></td>{if $address_label_payment}\n          <td> <p><font size="1"><strong><font face="Verdana, Arial, Helvetica, sans-serif">Paymentaddress<br />\n          </font> </strong></font></p></td>{/if}\n        </tr>\n        <tr> \n          <td><font size="1" face="Verdana, Arial, Helvetica, sans-serif">{$address_label_shipping}</font></td>\n          {if $address_label_payment}<td><font size="1" face="Verdana, Arial, Helvetica, sans-serif">{$address_label_payment}</font></td>{/if}\n        </tr>\n      </table>\n      <p><font size="1" face="Verdana, Arial, Helvetica, sans-serif"> Dear {$NAME},<br />\n        <br />\n thank you for your order. <br />\n {$PAYMENT_INFO_HTML}</font>\n        <br />{if $COMMENTS}<br />\n        <strong><font size="1" face="Verdana, Arial, Helvetica, sans-serif">Your Comments:</font></strong><br />\n        <font size="1" face="Verdana, Arial, Helvetica, sans-serif">{$COMMENTS}</font><br />\n{/if}<br />\n      </p></td>\n  </tr>\n</table>\n<table style="border-bottom:1px solid;" width="100%" border="0" cellpadding="0" cellspacing="0">\n  <tr> \n    <td><font size="1" face="Verdana, Arial, Helvetica, sans-serif"> <strong>Your ordered following products: </strong></font></td>\n  </tr>\n  <tr> \n    <td> <table width="100%" border="0" cellpadding="3" cellspacing="0" bgcolor="f1f1f1">\n        <tr> \n          <td colspan="2" style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;"><div align="center"><font size="1" face="Verdana, Arial, Helvetica, sans-serif"><strong><font size="1">\n            pcs</font></strong></font></div></td>\n          <td style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;"><font size="1" face="Verdana, Arial, Helvetica, sans-serif"><strong> \n          {if $smarty.const.SHOW_IMAGES_IN_EMAIL == ''true''}\n          <td style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;"><font size="1" face="Verdana, Arial, Helvetica, sans-serif"><strong> \n            Picture</strong></font></td>\n          {/if}\n            Product</strong></font></td>\n          <td style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;"><strong><font size="1" face="Verdana, Arial, Helvetica, sans-serif">Article \n            Nr. </font></strong> </td>\n          <td style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;" width="150"><font size="1" face="Verdana, Arial, Helvetica, sans-serif"><strong>Singleprice \n            </strong></font></td>\n          <td style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;" width="150"><div align="right"><font size="1" face="Verdana, Arial, Helvetica, sans-serif"><strong><font size="1">Price</font> \n              </strong></font></div></td>\n        </tr>\n        {foreach name=aussen item=order_values from=$order_data} \n        <tr> \n          <td width="20" style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;"><div align="center"><font size="1" face="Verdana, Arial, Helvetica, sans-serif">{$order_values.PRODUCTS_QTY}</font></div></td>\n          <td width="20" style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;"><div align="center"><font size="1" face="Verdana, Arial, Helvetica, sans-serif">x</font></div></td>\n          {if $smarty.const.SHOW_IMAGES_IN_EMAIL == ''true''}\n          <td style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;">\n            {if $order_values.PRODUCTS_IMAGE neq ''''}              \n                <img src="{$img_path}{$order_values.PRODUCTS_IMAGE}" style="{$smarty.const.SHOW_IMAGES_IN_EMAIL_STYLE}">              \n            {/if}\n          </td>\n          {/if}\n          <td style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;">\n            <font size="1" face="Verdana, Arial, Helvetica, sans-serif">\n            <strong>{$order_values.PRODUCTS_NAME}</strong>\n            {if $order_values.PRODUCTS_ORDER_DESCRIPTION neq ''''}<br />{$order_values.PRODUCTS_ORDER_DESCRIPTION}{/if}\n            {if $order_values.PRODUCTS_SHIPPING_TIME neq ''''}<br />Shipping time: {$order_values.PRODUCTS_SHIPPING_TIME}{/if}\n            {if $order_values.PRODUCTS_ATTRIBUTES neq ''''}<br /><em>{$order_values.PRODUCTS_ATTRIBUTES}</em>{/if}\n            </font>\n            </td>\n          <td style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;"><font size="1" face="Verdana, Arial, Helvetica, sans-serif">{$order_values.PRODUCTS_MODEL}<br />\n            <em>{$order_values.PRODUCTS_ATTRIBUTES_MODEL}</em></font></td>\n          <td style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;" width="150"><font size="1" face="Verdana, Arial, Helvetica, sans-serif">{$order_values.PRODUCTS_SINGLE_PRICE}</font></td>\n          <td style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;" width="150"><div align="right"><font size="1" face="Verdana, Arial, Helvetica, sans-serif">{$order_values.PRODUCTS_PRICE}</font></div></td>\n        </tr>\n        {/foreach} \n      </table>\n    </td>\n  </tr>\n</table>\n{foreach name=aussen item=order_total_values from=$order_total}\n<div align="right"><font size="1" face="Arial, Helvetica, sans-serif">{$order_total_values.TITLE}{$order_total_values.TEXT}</font></div>\n{/foreach}\n\n{if $DELIVERY_DUTY_INFO neq ''''}\n<br />\n<table style="border:1px solid #a3a3a3;" width="100%" border="0" cellpadding="0" cellspacing="0">\n  <tr>\n    <td><font size="1" face="Arial, Helvetica, sans-serif">{#text_duty_info#}</font></td>\n  </tr>\n</table>\n<br />\n{/if}\n\n[SIGNATUR]\n\n{if $REVOCATION_HTML neq ''''}\n<br />\n<font size="1" face="Arial, Helvetica, sans-serif">{$REVOCATION_HTML}</font>\n<br />\n{/if}', 0, 'mail', '{config_load file="$language/lang_$language.conf" section="duty_info"} \n{$address_label_customer}\n\n{if $PAYMENT_METHOD}Paymentmethod: {$PAYMENT_METHOD}{/if}\nOrder No.: {$oID}\nDate: {$DATE}\n{if $csID}Customer ID: {$csID}{/if}\nYour e-mail-address: {$EMAIL}\n----------------------------------------------------------------------\n\n\nDear {$NAME},\n\nthank you for your order.\n\n{$PAYMENT_INFO_TXT}\n\n{if $COMMENTS}\nYour Comments:\n{$COMMENTS}\n{/if}\n\nYour ordered following products\n----------------------------------------------------------------------\n{foreach name=aussen item=order_values from=$order_data} \n{$order_values.PRODUCTS_QTY} x {$order_values.PRODUCTS_NAME} {$order_values.PRODUCTS_PRICE}\n{if $order_values.PRODUCTS_ORDER_DESCRIPTION neq ''''}{$order_values.PRODUCTS_ORDER_DESCRIPTION}{/if}\n{if $order_values.PRODUCTS_SHIPPING_TIME neq ''''}Shipping time: {$order_values.PRODUCTS_SHIPPING_TIME}{/if}\n{if $order_values.PRODUCTS_ATTRIBUTES !=''''}{$order_values.PRODUCTS_ATTRIBUTES}{/if}\n\n{/foreach}\n\n{foreach name=aussen item=order_total_values from=$order_total}\n{$order_total_values.TITLE}{$order_total_values.TEXT}\n{/foreach}\n\n\n{if $address_label_payment}\nPaymentaddress\n----------------------------------------------------------------------\n{$address_label_payment}\n{/if}\nShippingaddress \n----------------------------------------------------------------------\n{$address_label_shipping}\n{if $DELIVERY_DUTY_INFO neq ''''}\n\n----------------------------------------------------------------------\n{#text_duty_info#}\n----------------------------------------------------------------------{/if}\n\n[SIGNATUR]\n\n{$REVOCATION_TXT}'),
(69, 'password_verification_mail', 1, '<table  width="100%" border="0" align="center" cellpadding="4" cellspacing="0">\n  <tr> \n    <td><p><font size="2" face="Verdana, Arial, Helvetica, sans-serif"><b>Please \n        confirm your password request!</b></p>\n      <p>To obtain a new password immediately, click the following link <a href="{$LINK}">{$LINK}</a>.<br />\n      Ater clicking the link you will receive another e-mail containing a new password which can be changed when you are logged in.\n    </td>\n  </tr>\n</table>\n\n 	  	 \n', 0, 'mail', '\nPlease confirm your password request!\n\nTo obtain a new password immediately, click the following link - {$LINK}.\n\nAter clicking the link you will receive another e-mail containing a new password which can be changed when you are logged in.\n'),
(70, 'send_gift_to_friend', 1, '<table  width="100%" border="0" align="center" cellpadding="4" cellspacing="0">\n  <tr>\n    <td style="border-bottom: 1px solid; border-color: #cccccc;"><div align="right"><img src="{$logo_path}logo.gif"></div></td>\n  </tr>\n  <tr>\n    <td> <p><font size="2" face="Verdana, Arial, Helvetica, sans-serif">---------------------------------------------------------------------------------------- \n        <br />\n        You''ve received a <b>{$AMMOUNT}</b> gift voucher ! <br />\n        ----------------------------------------------------------------------------------------<br />\n        <br />\n        This gift voucher was sent to you by: {$FROM_NAME}<br />\n        Message: <br />\n        <br />\n        {$MESSAGE}<br />\n        <br />\n        Your voucher code is: <strong>{$GIFT_CODE}</strong>.<br /> \n        Redeem your voucher when placing an order or simply by clicking the following link <a href="{$GIFT_LINK}">{$GIFT_LINK}</a>. \n        <br />\n        </font></p>\n      </td>\n  </tr>\n</table>\n\n 	  	 \n', 0, 'mail', '\n---------------------------------------------------------------------------------------- \nYOU''VE RECEIVED A {$AMMOUNT} GIFT VOUCHER!\n----------------------------------------------------------------------------------------\n\nThis gift voucher was sent to you by: {$FROM_NAME}\n\nMessage:\n{$MESSAGE}\n\nYour voucher code is: {$GIFT_CODE}\n\nRedeem your voucher when placing an order or simply by clicking the following link - {$GIFT_LINK}.'),
(71, 'sepa_info', 1, '<p>The invoice amount of {$PAYMENT_BANKTRANSFER_TOTAL} will be collected by using the SEPA Direct Debit with due date {$PAYMENT_BANKTRANSFER_DUE_DATE}<br />\nwith mandate {$PAYMENT_BANKTRANSFER_MANDATE_REFERENCE}<br />\nand for creditor identifier {$PAYMENT_BANKTRANSFER_CREDITOR_ID}<br />\nfrom your account {$PAYMENT_BANKTRANSFER_IBAN}<br />\nat {$PAYMENT_BANKTRANSFER_BANKNAME}.</p>\n<p>Please ensure that there are sufficient funds on your account to cover the payment.</p>', 0, 'mail', 'The invoice amount of {$PAYMENT_BANKTRANSFER_TOTAL} will be collected by using the SEPA Direct Debit with due date {$PAYMENT_BANKTRANSFER_DUE_DATE}\nwith mandate {$PAYMENT_BANKTRANSFER_MANDATE_REFERENCE}\nand for creditor identifier {$PAYMENT_BANKTRANSFER_CREDITOR_ID}\nfrom your account {$PAYMENT_BANKTRANSFER_IBAN}\nat {$PAYMENT_BANKTRANSFER_BANKNAME}.\n\nPlease ensure that there are sufficient funds on your account to cover the payment.'),
(72, 'sepa_mail', 1, '{config_load file="$language/lang_$language.conf" section="duty_info"} \n{$PAYMENT_INFO_HTML}<br />\n\n[SIGNATUR]', 0, 'mail', '{config_load file="$language/lang_$language.conf" section="duty_info"} \n\n{$PAYMENT_INFO_TXT}\n\n[SIGNATUR]'),
(73, 'signatur', 1, '<div><font size="1" face="Verdana, Arial, Helvetica, sans-serif">__________________________________________________________________<br />\n<br />\n<b>Company</b><br />\nAddress<br />\nLocation<br />\nHomepage<br />\nE-mail<br />\nPhone:<br />\nFax:<br />\nCEO:<br />\nVAT Reg No: <br /></font></div\n', 0, 'mail', '__________________________________________________________________\n\nCompany\nAddress\nLocation\nHomepage\nE-mail\nPhone:\nFax:\nCEO:\nVAT Reg No\n'),
(74, 'change_password_mail', 2, '<table cellspacing="0" cellpadding="4" width="100%" border="0" align="center">\r\n    <tbody>\r\n        <tr>\r\n            <td style="border-bottom: 1px solid; border-color: #cccccc;">\r\n            <div align="right"><img src="{$logo_path}logo.gif" alt="" /></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td>\r\n            <p><font size="2" face="Verdana, Arial, Helvetica, sans-serif"><strong>Sehr geehrter Kunde,</strong></font></p>\r\n            <p><font size="2" face="Verdana, Arial, Helvetica, sans-serif">Ihr Passwort wurde erfolgreich ge&auml;ndert.<br />\r\n            <br />\r\n            Ihre neuen Logindaten:</font></p>\r\n            <table width="100%" border="0" bgcolor="f1f1f1">\r\n                <tbody>\r\n                    <tr>\r\n                        <td><font size="1" face="Verdana, Arial, Helvetica, sans-serif"><strong>Ihre E-Mail-Adresse:</strong> {$EMAIL}             <br />\r\n                        <strong>Ihr neues Passwort:</strong> {$PASSWORD}         </font></td>\r\n                    </tr>\r\n                </tbody>\r\n            </table>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>', 0, 'mail', 'Sehr geehrter Kunde,\r\n\r\nIhr Passwort wurde erfolgreich ge'),
(75, 'create_account_mail', 2, '<table  width="100%" border="0" align="center" cellpadding="4" cellspacing="0">\n  <tr> \n    <td style="border-bottom: 1px solid; border-color: #cccccc;"><div align="right"><img src="{$logo_path}logo.gif"></div></td>\n  </tr>\n  <tr> \n    <td><font size="2" face="Verdana, Arial, Helvetica, sans-serif"><strong>Sehr geehrter Kunde,</strong></font> <br />\n      <br />\n      <font size="2" face="Verdana, Arial, Helvetica, sans-serif">Sie haben soeben Ihr Kundenkonto erfolgreich erstellt, als registrierter Kunde haben sie folgende Vorteile in unserem Shop.</font><br /> \n      <br />\n      <font size="2"><b><font face="Verdana, Arial, Helvetica, sans-serif">-Kundenwarenkorb</font></b><font face="Verdana, Arial, Helvetica, sans-serif"> - Jeder Artikel bleibt registriert bis Sie zur Kasse gehen, oder die Produkte aus dem Warenkorb entfernen.<br />\n      <b>-Adressbuch</b> - Wir k&ouml;nnen jetzt die Produkte zu der von Ihnen ausgesuchten Adresse senden. Der perfekte Weg ein Geburtstagsgeschenk zu versenden.<br />\n      <b>-Vorherige Bestellungen</b> - Sie k&ouml;nnen jederzeit Ihre vorherigen Bestellungen &uuml;berpr&uuml;fen.<br />\n      <b>-Meinungen &uuml;ber Produkte</b> - Teilen Sie Ihre Meinung zu unseren Produkten mit anderen Kunden.<br />      \n      </font></font><br />\n      <font size="2" face="Verdana, Arial, Helvetica, sans-serif">Falls Sie Fragen zu unserem Kunden-Service haben, wenden Sie sich bitte an: \n      {$content.MAIL_REPLY_ADDRESS}\n      . <br />\n    Achtung: Diese E-Mail-Adresse wurde uns von einem Kunden bekannt gegeben. Falls Sie sich nicht angemeldet haben, senden Sie bitte eine E-Mail an \n    {$content.MAIL_REPLY_ADDRESS}\n    . <br />\n    <br />\n	\n	\n    {if $SEND_GIFT==true}\n    <br />\n    Als kleines Willkommensgeschenk senden wir Ihnen einen Gutschein &uuml;ber:	<b>{$GIFT_AMMOUNT}</b><br />\n    <br />\nIhr pers&ouml;nlicher Gutscheincode lautet <b>{$GIFT_CODE}</b>. Sie k&ouml;nnen diese Gutschrift an der Kasse w&auml;hrend des Bestellvorganges verbuchen.<br />\n<br />\nUm den Gutschein einzul&ouml;sen klicken Sie bitte auf <a href="{$GIFT_LINK}">[Gutschein Einl&ouml;sen]</a>.\n{/if}\n\n{if $SEND_COUPON==true}\n Als kleines Willkommensgeschenk senden wir Ihnen einen Kupon.<br />\n Kuponbeschreibung: <b>{$COUPON_DESC}</b>\n \nGeben Sie einfach Ihren pers&ouml;nlichen Code {$COUPON_CODE} w&auml;hrend des Bezahlvorganges ein\n\n{/if}\n\n</font></td>\n  </tr>\n</table>\n', 0, 'mail', '\n\nSehr geehrter Kunde,\n\nSie haben soeben Ihr Kundenkonto erfolgreich erstellt, als registrierter Kunde haben sie folgende Vorteile in unserem Shop. \n\n-Kundenwarenkorb - Jeder Artikel bleibt registriert bis Sie zur Kasse gehen, oder die Produkte aus dem Warenkorb entfernen.\n-Adressbuch - Wir k'),
(76, 'new_password_mail', 2, '<table  width="100%" border="0" align="center" cellpadding="4" cellspacing="0">\n  <tr> \n    <td><p><font size="2" face="Verdana, Arial, Helvetica, sans-serif"><b>Passwort \n        ge&auml;ndert!</b></p> \n      <p>Sie erhalten diese E-Mail, weil Sie ein neues Passwort eingerichtet \n        bekommen wollten.<br />\n        Bitte loggen Sie sich mit folgendem Passwort und Ihrer E-Mail-Adresse \n        bei uns ein <br />\n        In Ihren Kontoeinstellungen k&ouml;nnen Sie ein neues Passwort vergeben.\n       </p>\n      <table width="100%"  border="0" bgcolor="f1f1f1">\n    <tr>\n      <td><b>Ihr neues Passwort:<br /></b>{$NEW_PASSWORD}\n      </td>\n    </tr>\n  </table>\n      <p>Wir w&uuml;nschen Ihnen weiterhin viel Spa&szlig; mit unserem Angebot! \n      </p>\n      </td>\n  </tr>\n</table>\n', 0, 'mail', '\nPasswort ge'),
(77, 'newsletter_mail', 2, '<p><font size="2" face="Verdana, Arial, Helvetica, sans-serif"><b>Vielen Dank f', 0, 'mail', '\nVielen Dank f'),
(78, 'order_mail', 2, '{config_load file="$language/lang_$language.conf" section="checkout_confirmation"} \n{config_load file="$language/lang_$language.conf" section="duty_info"} \n<table width="100%" border="0">\n  <tr> \n    <td>\n      <table width="100%" border="0" cellpadding="0" cellspacing="0">\n        <tr> \n          <td><font size="1" face="Verdana, Arial, Helvetica, sans-serif">{$address_label_customer}<br />\n            <br />\n            {if $PAYMENT_METHOD}<strong>Zahlungsmethode:</strong> {$PAYMENT_METHOD}<br />{/if}\n            <strong>Bestellung Nr:</strong> {$oID}<br />\n            <strong>Bestelldatum:</strong> {$DATE}<br />\n            {if $csID}<strong>Kundennummer:</strong> {$csID}<br />{/if}\n            <strong>Ihre E-Mail-Adresse:</strong> {$EMAIL}<br />\n            </font>\n          </td>\n          <td width="1"><img src="{$logo_path}logo.gif"></td>\n        </tr>\n      </table>\n      <br /> \n      <table style="border-top:1px solid; border-bottom:1px solid;" width="100%" border="0">\n        <tr bgcolor="#f1f1f1"> \n          <td width="50%"> \n            <p><font size="1"><strong><font face="Verdana, Arial, Helvetica, sans-serif">Lieferadresse <br /></font></strong></font></p>\n          </td>\n          {if $address_label_payment}\n          <td> \n            <p><font size="1"><strong><font face="Verdana, Arial, Helvetica, sans-serif">Rechnungsadresse <br /></font></strong></font></p>\n          </td>\n          {/if}\n        </tr>\n        <tr> \n          <td><font size="1" face="Verdana, Arial, Helvetica, sans-serif">{$address_label_shipping}</font></td>\n          {if $address_label_payment}\n          <td><font size="1" face="Verdana, Arial, Helvetica, sans-serif">{$address_label_payment}</font></td>\n          {/if}\n        </tr>\n      </table>\n      <p><font size="1" face="Verdana, Arial, Helvetica, sans-serif"> Hallo {$NAME},<br /><br /> vielen Dank f&uuml;r Ihre Bestellung.  \n        <br />{$PAYMENT_INFO_HTML}<br />\n        {if $COMMENTS}<br />\n        <strong>Ihre Anmerkungen:</strong><br />\n        {$COMMENTS}<br />\n        {/if}\n        <br />\n        </font>\n      </p>\n    </td>\n  </tr>\n</table>\n<table style="border-bottom:1px solid;" width="100%" border="0" cellpadding="0" cellspacing="0">\n  <tr> \n    <td><font size="1" face="Verdana, Arial, Helvetica, sans-serif"> <strong>Ihre bestellten Produkte nochmals zur Kontrolle: </strong></font></td>\n  </tr>\n  <tr> \n    <td> \n      <table width="100%" border="0" cellpadding="3" cellspacing="0" bgcolor="f1f1f1">\n        <tr> \n          <td colspan="2" style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;"><div align="center"><font size="1" face="Verdana, Arial, Helvetica, sans-serif"><strong> \n            Stk. </strong></font></div></td>\n          {if $smarty.const.SHOW_IMAGES_IN_EMAIL == ''true''}\n          <td style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;"><font size="1" face="Verdana, Arial, Helvetica, sans-serif"><strong> \n            Bild </strong></font></td>\n          {/if}\n          <td style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;"><font size="1" face="Verdana, Arial, Helvetica, sans-serif"><strong> \n            Produkt </strong></font></td>\n          <td style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;"><font size="1" face="Verdana, Arial, Helvetica, sans-serif"><strong> \n            Artikel Nr. </strong></font></td>\n          <td style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;" width="150"><font size="1" face="Verdana, Arial, Helvetica, sans-serif"><strong>Einzelpreis</strong></font></td>\n          <td style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;" width="150"><div align="right"><font size="1" face="Verdana, Arial, Helvetica, sans-serif"><strong> \n            Preis</strong></font><font size="1"> </font></div></td>\n        </tr>\n        {foreach name=aussen item=order_values from=$order_data} \n        <tr> \n          <td width="20" style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;"><div align="center"><font size="1" face="Verdana, Arial, Helvetica, sans-serif">{$order_values.PRODUCTS_QTY}</font></div></td>\n          <td width="20" style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;"><div align="center"><font size="1" face="Verdana, Arial, Helvetica, sans-serif">x</font></div></td>\n          {if $smarty.const.SHOW_IMAGES_IN_EMAIL == ''true''}\n          <td style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;">\n            {if $order_values.PRODUCTS_IMAGE neq ''''}              \n                <img src="{$img_path}{$order_values.PRODUCTS_IMAGE}" style="{$smarty.const.SHOW_IMAGES_IN_EMAIL_STYLE}">              \n            {/if}\n          </td>\n          {/if}\n          <td style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;">\n            <font size="1" face="Verdana, Arial, Helvetica, sans-serif">\n            <strong>{$order_values.PRODUCTS_NAME}</strong>\n            {if $order_values.PRODUCTS_ORDER_DESCRIPTION neq ''''}<br />{$order_values.PRODUCTS_ORDER_DESCRIPTION}{/if}\n            {if $order_values.PRODUCTS_ATTRIBUTES neq ''''}<br /><em>{$order_values.PRODUCTS_ATTRIBUTES}</em>{/if}\n            {if $order_values.PRODUCTS_ATTRIBUTES_DOWNLOAD == 0}\n                {if $order_values.PRODUCTS_SHIPPING_TIME neq ''''}<br />&nbsp;<br />Lieferzeit: {$order_values.PRODUCTS_SHIPPING_TIME}{/if}\n            {else}\n                {if $agree_download == ''agree''}\n                        {if $order_values.PRODUCTS_SHIPPING_TIME neq ''''}<br />&nbsp;<br />Lieferzeit: {$order_values.PRODUCTS_SHIPPING_TIME}{#text_download_agreed#}{/if}\n                {else}\n                        {if $order_values.PRODUCTS_SHIPPING_TIME neq ''''}<br />Lieferzeit: {#text_download_disagreed#}{/if}\n                {/if}\n            {/if}\n            </font>\n          </td>\n          <td style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;"><font size="1" face="Verdana, Arial, Helvetica, sans-serif">{$order_values.PRODUCTS_MODEL}<br />\n            <em>{$order_values.PRODUCTS_ATTRIBUTES_MODEL}</em></font></td>\n		<td style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;" width="150"><font size="1" face="Verdana, Arial, Helvetica, sans-serif">{$order_values.PRODUCTS_SINGLE_PRICE}<br />&nbsp;</font></td>\n		<td style="border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;" width="150"><div align="right"><font size="1" face="Verdana, Arial, Helvetica, sans-serif">{$order_values.PRODUCTS_PRICE}<br />&nbsp;</font></div></td>\n        </tr>\n        {/foreach} \n      </table>\n    </td>\n  </tr>\n</table>\n{foreach name=aussen item=order_total_values from=$order_total}\n<div align="right"><font size="1" face="Arial, Helvetica, sans-serif">{$order_total_values.TITLE}{$order_total_values.TEXT}</font></div>\n{/foreach}\n\n{if $DELIVERY_DUTY_INFO neq ''''}\n<br />\n<table style="border:1px solid #a3a3a3;" width="100%" border="0" cellpadding="3" cellspacing="0">\n  <tr>\n    <td><font size="1" face="Arial, Helvetica, sans-serif">{#text_duty_info#}</font></td>\n  </tr>\n</table>\n<br />\n{/if}\n\n[SIGNATUR]\n\n{if $REVOCATION_HTML neq ''''}\n<br />\n<font size="1" face="Arial, Helvetica, sans-serif">{$REVOCATION_HTML}</font>\n<br />\n{/if}\n\n', 0, 'mail', '{config_load file="$language/lang_$language.conf" section="duty_info"} \n{$address_label_customer}\n\n{if $PAYMENT_METHOD}Zahlungsmethode: {$PAYMENT_METHOD}{/if}\nBestellnummer: {$oID}\nDatum: {$DATE}\n{if $csID}Kundennummer: {$csID}{/if}\nIhre E-Mail-Adresse: {$EMAIL}\n----------------------------------------------------------------------\n\n\nHallo {$NAME},\n\nvielen Dank f'),
(79, 'password_verification_mail', 2, '<table  width="100%" border="0" align="center" cellpadding="4" cellspacing="0">\n  <tr> \n    <td><p><font size="2" face="Verdana, Arial, Helvetica, sans-serif"><b>Bitte best&auml;tigen Sie Ihre Passwortanfrage!</b></p>\n      <p>Bitte best&auml;tigen Sie, dass Sie selber ein neues Passwort \n        angefordert haben. <br />\n        Aus diesem Grund haben wir Ihnen diese E-Mail mit einem pers&ouml;nlichen \n        <br />\n        Best&auml;tigungslink geschickt. Wenn Sie den Link best&auml;tigen, indem \n        Sie ihn <br />\n        anklicken, wird Ihnen umgehend ein neues Passwort in einer weiteren \n        E-Mail <br />\n        zur Verf&uuml;gung gestellt. \n      <table width="100%"  border="0" bgcolor="f1f1f1">\n    <tr>\n          <td><b>Ihr Best&auml;tigungslink:<br />\n            </b><a href="{$LINK}">{$LINK}</a>\n      </td>\n    </tr>\n  </table>\n    </td>\n  </tr>\n</table>\n', 0, 'mail', '\nBitte best'),
(80, 'send_gift_to_friend', 2, '<table  width="100%" border="0" align="center" cellpadding="4" cellspacing="0">\n  <tr>\n    <td style="border-bottom: 1px solid; border-color: #cccccc;"><div align="right"><img src="{$logo_path}logo.gif"></div></td>\n  </tr>\n  <tr>\n    <td> <font size="2" face="Verdana, Arial, Helvetica, sans-serif">---------------------------------------------------------------------------------------- <br />\n  Herzlichen Gl&uuml;ckwunsch, Sie haben einen Gutschein &uuml;ber <b>{$AMMOUNT} </b>erhalten ! <br />\n----------------------------------------------------------------------------------------<br /> \n<br />\nDieser Gutschein wurde Ihnen &uuml;bermittelt von {$FROM_NAME},<br />\nMit der Nachricht:<br />\n<br />\n{$MESSAGE}<br />\n<br />\nIhr pers&ouml;nlicher Gutscheincode lautet <strong>{$GIFT_CODE}</strong>. Sie k&ouml;nnen diese Gutschrift entweder w&auml;hrend dem Bestellvorgang verbuchen.<br />\n<br />\nUm den Gutschein einzul&ouml;sen klichen Sie bitte auf <a href="{$GIFT_LINK}">{$GIFT_LINK}</a> <br />\n<br />\nFalls es mit dem obigen Link Probleme beim Einl&ouml;sen kommen sollte, <br />\nk&ouml;nnen Sie den Betrag w&auml;hrend des Bestellvorganges verbuchen. </font></td>\n  </tr>\n</table>\n', 0, 'mail', '----------------------------------------------------------------------------------------\n  Herzlichen Gl'),
(81, 'sepa_info', 2, '<p>Den Rechnungsbetrag von{$PAYMENT_BANKTRANSFER_TOTAL} ziehen wir als SEPA-Lastschrift zum F&auml;lligkeitstag {$PAYMENT_BANKTRANSFER_DUE_DATE}<br />\nzu Ihrer Mandatsreferenz {$PAYMENT_BANKTRANSFER_MANDATE_REFERENCE}<br />\nund unserer Gl&auml;ubiger-ID {$PAYMENT_BANKTRANSFER_CREDITOR_ID}<br />\nvon Ihrem Konto {$PAYMENT_BANKTRANSFER_IBAN}<br />\nbei der {$PAYMENT_BANKTRANSFER_BANKNAME} ein.<br /></p>\n<p>Bitte stellen Sie sicher, dass gen&uuml;gend Geld f&uuml;r die Zahlung auf dem Konto verf&uuml;gbar ist.</p>', 0, 'mail', 'Den Rechnungsbetrag von{$PAYMENT_BANKTRANSFER_TOTAL} EUR ziehen wir als SEPA-Lastschrift zum F'),
(82, 'sepa_mail', 2, '{config_load file="$language/lang_$language.conf" section="duty_info"} \n{$PAYMENT_INFO_HTML}<br />\n\n[SIGNATUR]', 0, 'mail', '{config_load file="$language/lang_$language.conf" section="duty_info"} \n\n{$PAYMENT_INFO_TXT}\n\n[SIGNATUR]'),
(83, 'signatur', 2, '<div><font size="1" face="Verdana, Arial, Helvetica, sans-serif">__________________________________________________________________<br />\n<br />\nFirma<br />\nAdresse<br />\nOrt<br />\nHomepage<br />\nE-Mail<br />\nFon:<br />\nFax:<br />\nUSt-IdNr.:<br />\nHandelsregister<br />\nGesch&auml;ftsf&uuml;hrer:<br /></font></div>', 0, 'mail', '__________________________________________________________________\n\nFirma\nAdresse\nOrt\nHomepage\nE-Mail\nFon:\nFax:\nUSt-IdNr.:\nHandelsregister\nGesch');

ALTER TABLE admin_access ADD products_content INT(1) NOT NULL DEFAULT '0' AFTER imagesliders;
ALTER TABLE admin_access ADD email_manager INT(1) NOT NULL DEFAULT '0' AFTER pdfbill_display;
ALTER TABLE admin_access ADD email_preview INT(1) NOT NULL DEFAULT '0' AFTER email_manager;
ALTER TABLE admin_access ADD wholesalers INT(1) NOT NULL DEFAULT '0' AFTER email_preview;
ALTER TABLE admin_access ADD wholesalers_list INT(1) NOT NULL DEFAULT '0' AFTER wholesalers;

ALTER TABLE countries ADD top tinyint(1) NOT NULL DEFAULT '0' AFTER status;

ALTER TABLE products ADD wholesaler_id INT(1) NOT NULL DEFAULT '0' AFTER products_startpage_sort;
ALTER TABLE products ADD wholesaler_reorder INT(4) NOT NULL DEFAULT '0' AFTER wholesaler_id;

DROP TABLE IF EXISTS wholesalers;
CREATE TABLE wholesalers (
  wholesaler_id INT NOT NULL,
  wholesaler_name VARCHAR(254) NOT NULL,
  wholesaler_email VARCHAR(254) NOT NULL,
  wholesaler_email_template VARCHAR(254) NOT NULL,
  PRIMARY KEY (wholesaler_id),
  KEY idx_wholesaler_id (wholesaler_id)
) ENGINE=MyISAM;

DROP TABLE IF EXISTS widgets;
CREATE TABLE widgets (
  widgets_id INT(11) NOT NULL AUTO_INCREMENT,
  widgets_path VARCHAR(255) NOT NULL,
  customer_id INT(11) DEFAULT NULL,
  widgets_x INT(11) DEFAULT NULL,
  widgets_y INT(11) DEFAULT NULL,
  widgets_active TINYINT(1) NOT NULL DEFAULT 1,
  PRIMARY KEY (widgets_id)
) ENGINE=MyISAM;

ALTER TABLE content_manager ADD change_date datetime DEFAULT NULL AFTER content_meta_keywords;
ALTER TABLE content_manager ADD content_meta_index tinyint(1) NOT NULL DEFAULT '1' AFTER change_date;

ALTER TABLE pdfbill_profile ADD rules text NOT NULL AFTER profile_categories;

UPDATE database_version SET version = 'SH_1.2.0';
